mapper.select:

select *
from MEMBER_INFO
where MEMBER_ID=#{memberId}
      and member_pwd=#{member_pwd}
      and member_status='Y'

select count(*)
from member_info
where MEMBER_ID=#{memberId}

mapper.update:
update member_info
set member_status='N'
where MEMBER_ID=#{memberId}

update member_info
set member_pwd=#{newPwd}
where MEMBER_ID=#{memberId}

update reserve_info
set reserve_status='N'
where RESERVE_NO=#{reserveNo}
